INSERT INTO `room_hotel_info` VALUES ('8121', '../img/room005.jpg', '大床房', '79%', '3600', '无早  双床', '双人自助早餐+双人珠江夜游门票', '3456', '￥5800', '我的挖掘机时尚时尚最时尚', 'img/dh1119.jpg', '3月8日', 'pearlosophy～很棒的酒店每次都会来住，还是这里的VIP很喜欢很安静，和W比较呢比较复古一点没有W那么年轻化，不一样的感觉。服务也是挺好的选择都是比较正确的，我还有十个字就写满100字点评了哈哈。OK了。', '10025');
INSERT INTO `room_hotel_info` VALUES ('8122', '../img/room006.jpg', '双床房', '71%', '2550', '有早 双床', '双人自助早餐+双人珠江夜游门票', '3128', '￥8300', '\r\n梦在深巷', 'img/dh1110.jpg', '4月6日', '天鹅酒店是一家國際性奢華酒店管理集團，總部位於加拿大多倫多市，1961年由伊薩多·夏普（Isadore Sharp）創辦，在40個國家擁有超過90家酒店，為全球最佳酒店集團之一。比爾·蓋茨和阿爾瓦利德·本·塔拉爾王子也是四季酒店集團的大股東。', '10025');
